62751195000_61783","model":"select_area_xian","rules":[]}]}],"options":{"gutter":0,"justify":"center","align":"top","remoteFunc":"func_1562751181000_94847"},"key":"1562751181000_94847","model":"grid_1562751181000_94847","rules":[]}],"config":{"labelWidth":100,"labelPosition":"top","size":"small","expand":{"js":"// 定义各个下拉框 model ，与表单设计器里填写的一致\nvar areaSheng = ''select_area_sheng''; // 省\nvar areaShi = ''select_area_shi''; // 市\nvar areaXian = ''select_area_xian''; // 县\n\n// 存储从后台查询而来的所有地区数据\nvar allArea = null\n\napi.get(''/api/json/area_mini'').then(function (res) {\n    allArea = [{}, {}, {}]\n\n    // 采用递归的方式将数据转成易处理的格式\n    var recursive = function (parentCode, children, level) {\n        for (var child of children) {\n            var parent = allArea[level][parentCode]\n            if (!parent) {\n                parent = []\n                allArea[level][parentCode] = parent\n            }\n            parent.push({ label: child.label, value: child.value })\n            // 若还有子级就继续递归\n            if (child.children instanceof Array) {\n                recursive(child.value, child.children, level + 1)\n            }\n        }\n    }\n    recursive(''0'', res, 0)\n    // 显示出第一级的数据\n    handleSelectChange(0, areaSheng, ''0'')\n})\n\n/* 处理watch事件 */\nfunction handleSelectChange(level, code, value) {\n    if (allArea == null) return;\n    var options = []\n    if (value != null) {\n        options = allArea[level][value];\n    }\n    api.setFormOptions(code, ''options'', options)\n}\n// 设置监听\napi.watch({\n    [areaSheng]: {\n        immediate: true,\n        handler: function (val) {\n            handleSelectChange(1, areaShi, val)\n\n            // 清空选择\n            api.setFormData(areaShi, '''')\n            api.setFormData(areaXian, '''')\n            // 清空县的选项\n            handleSelectChange(2, areaXian, null)\n        }\n    },\n    [areaShi]: {\n        immediate: true,\n        handler: function (val) {\n            handleSelectChange(2, areaXian, val)\n\n            // 清空选择\n            api.setFormData(areaXian, '''')\n        }\n    }\n})","css":""}}}', NULL, NULL, NULL, N'1', NULL, NULL, N'jeecg', N'2019-07-10 20:26:53.0000000', NULL, NULL)
