INSERT INTO [dbo].[onl_graphreport_head]  VALUES (N'27accd782f98c41eae5fad1b0514238d', N'月度增长分析同比', N'monthly_growth_analysis', N'SELECT
	id,
	[month],
	main_income AS main_income_2018,
	other_income AS other_income_2018,
	(
		main_income + other_income
	) AS [2018年月总收入],
(
		SELECT
			main_income
		FROM
			jeecg_monthly_growth_analysis AS year2
		WHERE
			year1.[month]= year2.[month]
		AND year2.[year] = ''2019''
	) AS [main_income_2019],
(
		SELECT
			other_income
		FROM
			jeecg_monthly_growth_analysis AS year2
		WHERE
			year1.[month] = year2.[month]
		AND year2.[year] = ''2019''
	) AS [other_income_2019],
(
		SELECT
			(
				main_income + other_income
			)
		FROM
			jeecg_monthly_growth_analysis AS year2
		WHERE
			year1.[month] = year2.[month]
		AND year2.[year] = ''2019''
	) AS [2019年月总收入]
FROM
	jeecg_monthly_growth_analysis year1
WHERE
	year1.[year] = ''2018''', N'month', N'2018年月总收入,2019年月总收入', N'-', N'', N'', N'table,line,bar', N'combination', N'double', N'sql', N'2019-04-28 16:47:37.0000000', N'admin', N'2019-04-28 16:48:07.0000000', N'admin')
GO

