INSERT INTO `onl_graphreport_head` VALUES ('0dbeb0dfc3ec18f0ce8d1d0caeeb6095', '统计近十日的登陆次数', 'login_count_charts', 'SELECT\n	count(*) num,\n	DATE_FORMAT(create_time, \'%Y-%m-%d\') AS `day`\nFROM\n	sys_log\nWHERE\n	log_type = 1\nAND create_time > DATE_SUB(NOW(), INTERVAL 10 DAY)\nGROUP BY\n	DATE_FORMAT(create_time, \'%Y-%m-%d\')', 'day', 'num', '登陆次数', '统计登陆', NULL, 'line,pie,bar,table', 'combination', 'tab', 'sql', '2019-04-11 14:36:30', 'admin', '2019-04-11 14:36:55', 'admin');
INSERT INTO `onl_graphreport_head` VALUES ('1c034f6063cee129e6aa32016ac42ce8', '项目性质收入统计SQL', 'project_statistics_sql', 'SELECT\n	id,\n	nature,\n	insurance_fee,\n	risk_consulting_fee,\n	evaluation_fee,\n	insurance_evaluation_fee,\n	bidding_consulting_fee,\n	interol_consulting_fee,\n	(\n		insurance_fee + \n		risk_consulting_fee + \n		risk_consulting_fee + \n		evaluation_fee + \n		insurance_evaluation_fee + \n		bidding_consulting_fee + \n		interol_consulting_fee\n	) AS \'total\'\nFROM\n	jeecg_project_nature_income', 'nature', 'total', '总计', NULL, NULL, 'pie,bar,table', 'combination', 'tab', 'sql', '2019-04-28 15:01:30', 'admin', '2019-04-28 15:01:44', 'admin');
INSERT INTO `onl_graphreport_head` VALUES ('27accd782f98c41eae5fad1b0514238d', '月度增长分析同比', 'monthly_growth_analysis', 'SELECT\n	`id`,\n	`month`,\n	`main_income` AS \'main_income_2018\',\n	`other_income` AS \'other_income_2018\',\n	(\n		`main_income` + `other_income`\n	) AS \'2018年月总收入\',\n	(\n		SELECT\n			`main_income`\n		FROM\n			`jeecg_monthly_growth_analysis` AS `year2`\n		WHERE\n			`year1`.`month` = `year2`.`month`\n		AND `year2`.`year` = \'2019\'\n	) AS \'main_income_2019\',\n	(\n		SELECT\n			`other_income`\n		FROM\n			`jeecg_monthly_growth_analysis` AS `year2`\n		WHERE\n			`year1`.`month` = `year2`.`month`\n		AND `year2`.`year` = \'2019\'\n	) AS \'other_income_2019\',\n	(\n		SELECT\n			(\n				`main_income` + `other_income`\n			)\n		FROM\n			`jeecg_monthly_growth_analysis` AS `year2`\n		WHERE\n			`year1`.`month` = `year2`.`month`\n		AND `year2`.`year` = \'2019\'\n	) AS \'2019年月总收入\'\nFROM\n	`jeecg_monthly_growth_analysis` AS `year1`\nWHERE\n	`year1`.`year` = \'2018\';', 'month', '2018年月总收入,2019年月总收入', '-', NULL, NULL, 'table,line,bar', 'combination', 'double', 'sql', '2019-04-28 16:47:37', 'admin', '2019-04-28 16:48:07', 'admin');
